str = ""

str = str.gsub(/0{40,}/, '|') # Space between words is 7 units
str = str.gsub(/1{15,}/, '-') # Dash is 3 units
str = str.gsub(/1{4,10}/, '.') # Dot is one unit
str = str.gsub(/0{15,25}/, ' ') # Space between letters is 3 units
str = str.gsub(/0{4,10}/, '') # Space between parts of the same letter is one unit
